matrix = String.split(input, "\n") |> Enum.map(&String.split(&1, "", trim: true))

# src https://stackoverflow.com/a/42887944/4858751
transpose = fn rows ->
  rows
  |> List.zip()
  |> Enum.map(&Tuple.to_list/1)
end

double_empty_rows = fn rows ->
  for row <- rows, reduce: [] do
    acc ->
      acc ++
        cond do
          Enum.all?(row, &(&1 == ".")) -> List.duplicate(row, 2)
          true -> [row]
        end
  end
end

expanded_matrix =
  matrix |> double_empty_rows.() |> transpose.() |> double_empty_rows.() |> transpose.()

galaxy_indexes =
  for {row, i} <- Enum.with_index(expanded_matrix),
      {cell, j} <- Enum.with_index(row),
      cell == "#" do
    {i, j}
  end

ans =
  for {{ax, ay}, i} <- Enum.with_index(galaxy_indexes),
      {{bx, by}, j} <- Enum.with_index(galaxy_indexes),
      i < j do
    abs(ax - bx) + abs(ay - by)
  end
  |> Enum.sum()

IO.inspect(ans)
