matrix = String.split(input, "\n") |> Enum.map(&String.split(&1, "", trim: true))

# src https://stackoverflow.com/a/42887944/4858751
transpose = fn rows ->
  rows
  |> List.zip()
  |> Enum.map(&Tuple.to_list/1)
end

find_empty_indexes = fn rows ->
  for {row, i} <- Enum.with_index(rows), reduce: [] do
    acc ->
      acc ++
        cond do
          Enum.all?(row, &(&1 == ".")) -> [i]
          true -> []
        end
  end
end

empty_row_indexes = find_empty_indexes.(matrix)
empty_col_indexes = matrix |> transpose.() |> find_empty_indexes.()

galaxy_indexes =
  for {row, i} <- Enum.with_index(matrix),
      {cell, j} <- Enum.with_index(row),
      cell == "#" do
    {i, j}
  end

ans =
  for {{ax, ay}, i} <- Enum.with_index(galaxy_indexes),
      {{bx, by}, j} <- Enum.with_index(galaxy_indexes),
      i < j do
    abs(ax - bx) + abs(ay - by) +
      (Enum.filter(empty_row_indexes, &(min(ax, bx) < &1 && &1 < max(ax, bx))) |> length()) *
        999_999 +
      (Enum.filter(empty_col_indexes, &(min(ay, by) < &1 && &1 < max(ay, by))) |> length()) *
        999_999
  end
  |> Enum.sum()

IO.inspect(ans)
